import React, { Component } from 'react';
import Navbar from '../Navbar/Navbar';

import "./confirmation.scss";


class Confirmation extends Component{
    saveAndContinue = (e) => {
        e.preventDefault();
        this.props.nextStep();
    }

    back  = (e) => {
        e.preventDefault();
        this.props.prevStep();
    }

    render(){
        const {values: { productLink, productImage, productName, productDetails, price, quantity, amount, deliveryFrom, deliveryTo, deliveryDate }} = this.props;

        return(
            <div>

                <Navbar conditions={'Order'}/>

                <div className="confirmation-order-form">

                <div className="confirmation-form-content">

                <img id="confirmation-img" src="https://image.flaticon.com/icons/svg/1004/1004765.svg" alt="confirmation"/>

                <h2 id="confirmation-order-header">Confirm your Details</h2>
                <p id="confirmation-p">Click confirm if the following details have been correctly entered</p>

            <div className="confirmation-col">

                        <p className="confirmation-li">Product Link: {productLink}</p>
                        <p className="confirmation-li">Product Image: {productImage}</p>
                        <p className="confirmation-li">Product Name: {productName}</p>
                        <p className="confirmation-li">Product Details: {productDetails}</p>
                <hr className="hr-confirmation"/>
                        <p className="confirmation-li"> Delivery From: {deliveryFrom}</p>
                        <p className="confirmation-li"> Delivery To: {deliveryTo}</p>
                        <p className="confirmation-li"> Order Date: {deliveryDate}</p>
            </div>



                <div className="confirmation-col">
                        <p className="confirmation-li"> Price: {price}</p>
                        <p className="confirmation-li"> Quantity: {quantity}</p>
                        <p className="confirmation-li"> Total: {price*quantity}</p>
                </div>



                <input className="confirmation-button" type="submit" value="Back" onClick={this.back} />
                <form onSubmit={this.props.confirmation}>
                <input className="confirmation-button" type="submit" value="Confirm"/>
                {/* <input id="order-button" type="submit" value="Confirm" onClick={this.saveAndContinue} /> */}
                </form>
                </div>
                </div>
            </div>
        )
    }
}

export default Confirmation;
