import React, { Component } from 'react'
import TravelService from '../service/travel-service';


export default class NewDelivery extends Component {

    constructor(props){
        super(props);
        this.state = { travelFrom: '', travelTo: '', date: ''};
        this.service = new TravelService();
      }

    handleFormSubmit = (event) => {
        event.preventDefault();

        this.service.sendTravels( this.state)
        .then( travel => {
            this.setState({ 
            travelFrom: "", 
            travelTo: "",
            date: ""
          });
        })
        .catch( error => console.log(error) )
    }

      handleChange = (event) => {  
        const {name, value} = event.target;
        this.setState({[name]: value});
      }

  render() {
    return (
      <div>

        <form onSubmit={this.handleFormSubmit}>

          <h2>Travel</h2>

          <div>

            <input className="" type="text" name="travelFrom" placeholder="Travel From" value={this.state.travelFrom} onChange={ e => this.handleChange(e)}/>            

            <input className="" type="text" name="travelTo" placeholder="Travel To" value={this.state.travelTo} onChange={ e => this.handleChange(e)}/>

            <input className="" type="text" name="date" placeholder="Date" value={this.state.date} onChange={ e => this.handleChange(e)}/>

            <input type="submit" value="Create travel" />

            </div>

          </form>

      </div>
    )
  }
}
