import React, { Component } from 'react'
import TravelService from '../service/travel-service';
import { Link, Redirect } from 'react-router-dom';


import Navbar from '../Navbar/Navbar';



export default class NewDelivery extends Component {

    constructor(props){
        super(props);
        this.state = { travelFrom: '', travelTo: '', travelDate: '', hasBeenSent: false};
        this.service = new TravelService();
      }

    handleFormSubmit = (event) => {
        event.preventDefault();

        this.service.sendTravels( this.state)
        .then( travel => {
            this.setState({ 
            travelFrom: "", 
            travelTo: "",
            travelDate: "",
            hasBeenSent: true
          });
        })
        .catch( error => console.log(error) )
    }

      handleChange = (event) => {  
        const {name, value} = event.target;
        this.setState({[name]: value});
      }

  render() {
    if (this.state.hasBeenSent === false){
        return (
      <div>

        <Navbar conditions={'newDelivery'}/>

      <div className="">
        <form onSubmit={this.handleFormSubmit}>

          <h2>Travel</h2>

          <div>

            <input className="" type="text" name="travelFrom" placeholder="Travel From" value={this.state.travelFrom} onChange={ e => this.handleChange(e)}/>            

            <input className="" type="text" name="travelTo" placeholder="Travel To" value={this.state.travelTo} onChange={ e => this.handleChange(e)}/>

            <input className="" type="date" name="travelDate" placeholder="Travel Date" value={this.state.travelDate} onChange={ e => this.handleChange(e)}/>

            <input type="submit" value="Create travel" />

            </div>

          </form>
          </div>
      </div>
    )
  } else {

    return <Redirect to="/travels" />
  }
}
}
